const eris = require('eris');

let bot = new eris(require('../../shinka/config.json').token);

bot.on('ready', () => {
    bot.editStatus('invisible')
    console.log('connected to discord');
})

bot.connect();

process.on("SIGINT", () => {
    bot.editStatus('invisible')
    bot.disconnect({reconnect: false});
    process.exit(0);
})

module.exports = bot;